namespace SkyHub\Api\Service;

/**
 * Class ServiceMultipart
 *
 * @package SkyHub\Api\Service
 */
class ServiceMultipart extends ServiceDefault
{
    /**
     * Service constructor.
     *
     * @param string $baseUri
     * @param array  $headers
     * @param array  $options
     */
    public function __construct($baseUri, array $headers = [], array $options = [], $log = true)
    {
        $headers['Accept']       = 'application/json';
        $headers['Content-Type']         = 'multipart/form-data';
        parent::__construct($baseUri, $headers, $options, $log);
    }

    /**
     * @param array $bodyData
     *
     * @return $this
     */
    protected function prepareRequestBody($bodyData, array &$options = [])
    {
        $options[OptionsBuilderInterface::BODY_TYPE_DEFAULT] = $bodyData;
        return $options;
    }
}
